final class ProductsModel {

	// ->

	private $connection = null;

	//============================================================
	public function __construct() {

		//--
		$this->connection = new \SmartSQliteDb('#db/shop.sqlite');
		//--
		$this->connection->open();
		//--

		//-- init (create) the sample tables if they do not exist
		$this->init_table();
		//--

	} //END FUNCTION
	//============================================================


	//============================================================
	public function __destruct() {

		//--
		if(!$this->connection instanceof \SmartSQliteDb) {
			return;
		} //end if
		//--
		$this->connection->close(); // clean shutdown
		//--

	} //END FUNCTION
	//============================================================


	//============================================================
	public function getConnection() {
		//--
		return $this->connection;
		//--
	} //END FUNCTION
	//============================================================


	//============================================================
	public function getData(int $limit, int $offset) {
		//--
		if($limit < 1) {
			$limit = 1;
		} elseif($limit > 25) {
			$limit = 25;
		} //end if
		//--
		if($offset < 0) {
			$offset = 0;
		} //end if
		//--
		return (array) $this->connection->read_adata(
			'SELECT * FROM products ORDER BY name ASC LIMIT '.(int)$limit.' OFFSET '.(int)$offset
		);
		//--
	} //END FUNCTION
	//============================================================
	public function countData() {
		//--
		return (int) $this->connection->count_data(
			'SELECT COUNT(*) FROM products'
		);
		//--
	} //END FUNCTION
	//============================================================


	//============================================================
	public function searchData(string $search, int $limit, int $offset) {
		//--
		$search = (string) str_replace('%', '', (string)$search); // inlocuieste '%' cu '' in $search
		$search = (string) trim((string)$search); // trim scoate afara spatiile de la inceput si sfarsit
		if((string)$search == '') {
			return array(); // nu avem expresie de cautare
		} //end if
		//--
		if($limit < 1) {
			$limit = 1;
		} elseif($limit > 25) {
			$limit = 25;
		} //end if
		//--
		if($offset < 0) {
			$offset = 0;
		} //end if
		//--
		return (array) $this->connection->read_adata(
			'SELECT * FROM products WHERE ((name LIKE ?) OR (class LIKE ?)) ORDER BY name ASC LIMIT '.(int)$limit.' OFFSET '.(int)$offset,
			[
				(string) '%'.$search.'%',
				(string) '%'.$search.'%'
			]
		);
		//--
	} //END FUNCTION
	//============================================================
	public function countSearchData($search) {
		//--
		$search = (string) str_replace('%', '', (string)$search); // inlocuieste '%' cu '' in $search
		$search = (string) trim((string)$search); // trim scoate afara spatiile de la inceput si sfarsit
		if((string)$search == '') {
			return 0; // nu avem expresie de cautare
		} //end if
		//--
		return (int) $this->connection->count_data(
			'SELECT COUNT(*) FROM products WHERE ((name LIKE ?) OR (class LIKE ?))',
			[
				(string) '%'.$search.'%',
				(string) '%'.$search.'%'
			]
		);
		//--
	} //END FUNCTION
